var hijackresponse = require('hijackresponse');
var bl = require('bl');
var LRU = require('lru-cache');
var posthtmlPreload = require('./posthtml-preload');

function encodeRFC5987(str) {
  return encodeURI(str)
    .replace(/['()]/g, escape)
    .replace(/\*/g, '%2A')
    .replace(/%(?:7C|60|5E)/g, unescape);
}

